package assingment2;

public class problem9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5 ,b=10;
		System.out.println(">:"+(a>b));
		System.out.println("<:"+(a<b));
		System.out.println(">=:"+(a>=b));
		System.out.println("<:"+(a<=b));
		System.out.println("==:"+(a==b));
		System.out.println(">:"+(a!=b));
		
	}


	}


