package statements;

public class divisible {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		if (a%5==0 && a%11==0)
		{
			System.out.println("it is divisible by 5 and 11");
		}
		else
		{
			System.out.println("it is not divisible by 5 and 11");
		}
		
		
	}

}
